def validator_slash(pct_network):
    base = 1
    correlated = np.floor(pct_network / 100 * 3 * 32)
    return np.minimum(32, base + correlated)


if __name__ == '__main__':
    pct_network_slashed = np.arange(0, 25, 0.1)

    lido_insurance = TokenSteth.functions.balanceOf(
        '0x8B3f33234ABD88493c0Cd28De33D583B70beDe35').call() / 1e18
    # Buffer calculation: https://dune.com/queries/2481449; not sure if I'm exact, but close.
    lido_buffer = (
        CLIENT.eth.get_balance('0xae7ab96520DE3A18E5e111B5EaAb095312D7fE84') +  # stETH token
        CLIENT.eth.get_balance('0xB9D7934878B5FB9610B3fE8A5e441e8fad7E293f') +  # withdrawal vault
        CLIENT.eth.get_balance('0x388C818CA8B9251b393131C08a736A67ccB19297')  # EL reward vault
    ) / 1e18
    steth_total_value = TokenSteth.functions.totalSupply().call() / 1e18
    steth_num_validators = (steth_total_value - lido_buffer) // 32
    lido_loss = (steth_num_validators * (pct_network_slashed / 100) *
                 validator_slash(pct_network_slashed)) - lido_insurance
    lido_pct_loss = 100 * (lido_loss / steth_total_value)

    rp_eb8_ct = 0
    rp_eb16_ct = 0
    num_nodes = RocketNodeManager.functions.getNodeCount().call()
    for offset in range(0, num_nodes, 100):
        print(offset)
        node_ls = RocketNodeManager.functions.getNodeAddresses(offset, 100).call()
        for addr in node_ls:
            provided_eth = RocketNodeStaking.functions.getNodeETHProvided(addr).call()
            matched_eth = RocketNodeStaking.functions.getNodeETHMatched(addr).call()
            eb8_ct = (matched_eth - provided_eth) / 16e18
            eb16_ct = (provided_eth - (8e18 * eb8_ct)) / 16e18
            rp_eb8_ct += eb8_ct
            rp_eb16_ct += eb16_ct
            # since only 8 and 16-ETH bonds exist, the above are enough to figure out counts
            # this method doesn't account for withdrawn

    print(rp_eb8_ct)
    print(rp_eb16_ct)

    reth_total_supply = TokenReth.functions.totalSupply().call()
    reth_total_value = TokenReth.functions.getEthValue(reth_total_supply).call() / 1e18
    reth_total_supply /= 1e18
    print(reth_total_value)
    print(reth_total_supply)
    rp_loss = []
    rp_pct_loss = []
    for pct_slash in pct_network_slashed:
        slash_eth = validator_slash(pct_slash)
        if slash_eth <= 8:
            # NO bond fully covers
            loop_rp_loss = 0
        elif 8 < slash_eth <= 16:
            # NO bond fully covers for eb16
            loop_rp_loss = (rp_eb8_ct * pct_slash / 100) * (slash_eth - 8)
        elif 16 < slash_eth <= 24:
            loop_rp_loss = (rp_eb8_ct * pct_slash / 100) * (slash_eth - 8) + (
                rp_eb16_ct * pct_slash / 100) * (slash_eth - 16)
        else:
            # this is a tricky scenario:
            # - RP uses lump sum of 8+ ETH to determine a full exit
            # - if we get slashed hard enough, we won't see that
            # - in that case it's treated like CL rewards
            # - reth gets a share based on borrowed eth and commission
            a = 24 - ((32 - slash_eth) * (.86 * 24 / 32))
            b = 16 - ((32 - slash_eth) * (.86 * 16 / 32))
            loop_rp_loss = (rp_eb8_ct * pct_slash / 100) * a + (rp_eb16_ct * pct_slash / 100) * b
        loop_rp_pct_loss = 100 * (loop_rp_loss / reth_total_value)
        rp_loss.append(loop_rp_loss)
        rp_pct_loss.append(loop_rp_pct_loss)

    fig, ax = plt.subplots(1)
    ax.plot(pct_network_slashed, lido_pct_loss, color=COLOR_LIDO, label='stETH')
    ax.plot(pct_network_slashed, rp_pct_loss, color=COLOR_RP, label='rETH')
    ax.set_xlabel('Percent of network slashed')
    ax.set_ylabel('Percent loss experienced by LST')
    ax.legend()
    ax.grid()
    fig.savefig('./black_swan_insurance.png', bbox_inches='tight')

